    `_name`        varchar(50) COLLATE utf8_unicode_ci NOT NULL,
    `_prefix`      varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
    `_province_id` int(10) UNSIGNED                    DEFAULT NULL,
